package com.capgemini.training.day1;
public class ForEg {

 

    /**
     * @param args
     */
    public static void main(String[] args) {
        // TODO Auto-generated method stub
        for (int i = 0,j=10; i < 10 && j>0; i++,j--) {
            System.out.println(i*j);
        }

 

    }

 

}